def my_func(input1:str):
    if len(input1) > 20:
        return "Invalid input length"

    words = input1.split()
    output_words = []

    for word in words:
        # Check the length of the word
        if len(word) <= 4 or len(word) >= 8:
            output_words.append(word[::-1])
        else:
            output_words.append(word)

    return ' '.join(output_words)
