def my_func(input1: str):
    if len(input1) > 20:
        return "Input length exceeds limit"
    words = input1.split()
    result = []
    for word in words:
        if len(word) > 5:
            result.append(word[::-1])
        else:
            result.append(word)
    return ' '.join(result)
