def my_func(input1: str):
    if len(input1) > 20:
        return "Input length exceeds the allowed limit"

    words = input1.split()
    result = []
    for word in words:
        if len(word) <= 4:
            result.append(word)
        else:
            result.append(word[::-1])
    return ' '.join(result)
