def my_func(input1: str):
    if len(input1) > 20:
        return "Input exceeds maximum length"
    words = input1.split()
    reversed_words = []
    for word in words:
        if len(word) <= 4:
            reversed_words.append(word)
        else:
            reversed_words.append(word[::-1])
    return ' '.join(reversed_words)
