def my_func(input1: str):
    if len(input1) > 20:
        return "Input length exceeds 20 characters."
    words = input1.split()
    transformed_words = []
    for word in words:
        if len(word) > 5:
            transformed_words.append(word[::-1])
        else:
            transformed_words.append(word)
    return ' '.join(transformed_words)
