def my_func(input1: str, input2: str, input3: str) -> str:
    if input1 == input2 == input3:
        return input1

    if input3 == "nan":
        return "nan"

    max_len = max(len(input1), len(input2), len(input3))
    output = []

    for i in range(max_len):
        if i < len(input3) and input3[i] in input1:
            idx = input1.index(input3[i])
